    private function addTags($category, $tags) {
        $cat = Category::select('id')->where('name', $category)->first();
        foreach ($tags as $tag) {
            if (Tag::where(['name' => $tag])->first() == null) {
                Tag::insert(['name' => $tag, 'category_id' => $cat->id]);
            }
        }
    }
